// App.js

import React, { useEffect, useState } from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Typing from './components/Typing';
import ProgressBar from './components/ProgressBar';
import manojImage from './assets/manoj.jpg';
import ProjectTile from './components/ProjectTile';
import SkillsPage from './components/SkillsPage'; // Import the SkillsPage component
import { fetchGitHubProjects } from './utils/GitHubUtil';

function App() {
  const [scrollProgress, setScrollProgress] = useState(0);
  const [projects, setProjects] = useState([]);

  const handleScroll = () => {
    const totalScroll = document.documentElement.scrollTop;
    const windowHeight =
      document.documentElement.scrollHeight - document.documentElement.clientHeight;
    const scroll = (totalScroll / windowHeight) * 100;

    setScrollProgress(scroll);
  };

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    fetchGitHubProjects().then(data => {
      // Filter for the two specific repositories
      const selectedRepositories = data.filter(repo => (
        repo.name === 'Quiz-Game' || repo.name === 'Finance Tracker'
      ));
      setProjects(selectedRepositories);
    });
  }, []);

  return (
    <div className="App">
      <ProgressBar progress={scrollProgress} />
      <NavBar scrollToSection={scrollToSection} />
      <section id="home" className="section home-section">
        <div className="home-content">
          <div className="left">
            <img src={manojImage} alt="Manoj" className="manoj-image" />
          </div>
          <div className="right">
            <header className="App-header">
              <h1>HI I AM MANOJ</h1>
              <Typing
                text={[
                  'Software Developer.',
                  'AI/ML Enthusiast.',
                  'Full Stack Developer.',
                ]}
                typingSpeed={100}
                deletingSpeed={50}
                duration={1000}
              />
            </header>
          </div>
        </div>
      </section>
      <section id="about" className="section about-section">
        <div className="about-container">
          <div className="about-content">
            <h1>About Me</h1>
            <p>
              Hello! I'm <strong>Manoj Krishna</strong>, a tech and web development enthusiast who loves to build things.
            </p>
            <p>
              I'm currently a Computer Science student at <strong>Scaler School of Technology</strong>.
            </p>
            <p>
              Feel free to connect with me on <a href="https://www.linkedin.com">LinkedIn</a> or check out my projects on <a href="https://github.com">GitHub</a>.
            </p>
          </div>
        </div>
      </section>
      <h1 className="section-heading">Projects</h1>
      <section id="projects" className="section projects-section">
        <div className="project-list">
          {projects.map(project => (
            <ProjectTile key={project.id} project={project} />
          ))}
        </div>
      </section>
      <SkillsPage /> {/* Render the SkillsPage component */}
    </div>
  );
}

export default App;
