import React, { useEffect, useState } from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Typing from './components/Typing';
import ProgressBar from './components/ProgressBar';
import manojImage from './assets/manoj.jpg';

function App() {
    const [scrollProgress, setScrollProgress] = useState(0);

    const handleScroll = () => {
        const totalScroll = document.documentElement.scrollTop;
        const windowHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const scroll = (totalScroll / windowHeight) * 100;

        setScrollProgress(scroll);
    };

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    return (
        <div className="App">
            <ProgressBar progress={scrollProgress} />
            <NavBar />
            <section id="home" className="section">
                <div className="home-content">
                    <div className="left">
                        <img src={manojImage} alt="Manoj" className="manoj-image" />
                    </div>
                    <div className="right">
                        <header className="App-header">
                            <h1>HI I AM MANOJ</h1>
                            <Typing 
                                text={[
                                    "Software Developer.",
                                    "AI",
                                    "This is an example of typing and deleting text.",
                                ]}
                                typingSpeed={100}
                                deletingSpeed={50}
                                duration={1000}
                            />
                        </header>
                    </div>
                </div>
            </section>
            <section id="about" className="section">
                <h1>About Me</h1>
                <p>Hello! I'm Manoj, a passionate software developer with a knack for creating innovative solutions...</p>
                <p>Feel free to connect with me on <a href="https://www.linkedin.com">LinkedIn</a> or check out my projects on <a href="https://github.com">GitHub</a>.</p>
            </section>
            <section id="projects" className="section">
                <h1>Projects</h1>
                <p>Project section content.</p>
            </section>
            <section id="resume" className="section">
                <h1>Resume</h1>
                <p>Resume section content.</p>
            </section>
            <section id="skills" className="section">
                <h1>Skills</h1>
                <p>Skills section content.</p>
            </section>
        </div>
    );
}

export default App;
